@Test
    @DisplayName("should return the user when the user id is valid")
    void getUserByIdWhenUserIdIsValid() {
        User user = new User("John", "Smith", "john@smith.com");
        when(userDao.getUserById(1L)).thenReturn(user);

        User result = userDao.getUserById(1L);

        assertEquals(user, result);
    }

@Test
    @DisplayName("should return null when the user id is not found")
    void getUserByIdWhenUserIdNotFound() {
        Long id = 1L;
        when(userDao.getUserById(id)).thenReturn(null);
        assertNull(userDao.getUserById(id));
    }
